  totalTaskCount =  await res.json() ;
  // settotalTaskCount(totalTaskCount);
  // settotalTaskCompletedCount(totalTaskCompletedCount);
  // return {
  //   'totalTaskCount': totalTaskCount, 'totalTaskCompletedCount': totalTaskCompletedCount
