import React, {useState, useEffect} from 'react'
import ReactDOM from 'react-dom';
// import registerServiceWorker from './registerServiceWorker';
import Dropdown from './dropdownmenu/Dropdown.js';


// var displayDropdown = (
//   <div style={{display: 'flex', justifyContent: 'center'}} >
//     <Dropdown />
//   </div>
//   );

// ReactDOM.render(displayDropdown, document.getElementById('root'));

// registerServiceWorker();


function App() {
  return (
    <div>
     <List />
    </div>
  );
}


// The two functions below are how we use a GET function from the backend 
// to display them onto our FRONT END BOOM ------------------------------------------------
const List = () => {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState([]);
  const [date, setDate] = useState([]);

  // makeAjax request to FETCH TODOS ===================
  const fetchTodos = async () => {
    //fetch request goes here:
    const res = await fetch('http://localhost:3001/todos');
    setTodos(await res.json()); // turn response into json
  };

  // makeAjax request to Add TODOS -- onclick ===================
  const addTodo = async () => {
    //fetch request goes here:
    const res = await fetch('http://localhost:3001/todos', {
      method:'POST',
      headers: {
        'Content-Type': 'application/json'
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify({ desc: text, done: false, due: date})
    }
    );

    // currently, after we add a TODO, we must manuallly referesh our page to see the new Todos.
    // We'll insert the following lines in order to have a an aut-refers
    // assume the todo is successfully added a(and dont do any errro hadnling)
    fetchTodos(); // this wil fetch them and reflect them ont othe scren
    setText('');
  };

    // // deleteAllTodos
    const deleteAllTodos = async () => {
      await fetch(`http://localhost:3001/todos`, {
        method:'DELETE'
      });
      fetchTodos();
    }

    const StartTemplateToDoList = async () => {
      deleteAllTodos();
      await fetch(`http://localhost:3001/todos/standardTemplate`, {
        method:'POST',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      fetchTodos();
    }

  useEffect(() => {
    fetchTodos();
  }, []);

  const items = todos.map(todo => <Item todo={todo} key={todo._id} fetchTodos={fetchTodos}/> )

  return <div className="ui card">
  <div className="content">
    <div className="header">My To-Do List</div>
    <button className="positive ui button" onClick={deleteAllTodos}>Clear & Start Afresh</button>
    <button className="positive ui button" onClick={StartTemplateToDoList}>Start Standardized ToDo List</button>

  </div>
  <div className="content">

    <div className="ui relaxed divided list">
      {items}
    </div>
  </div>
  <div className="extra content">
    <div className="fluid ui action input">

          <div class="ui cards">
            <div class="card">
              {/* <div class="content"> */}
              <list style={{color:"blue"}}>
                <input type="text" value={text} onChange={(e) => setText(e.target.value)} placeholder="Task Name"/>
                <br></br>
                <div class="ui dropdown" >
                          <div class="text">Task Category</div>
                          <i class="dropdown icon"></i>
                          <div class="menu">
                            <div class="item">Academic</div>
                            <div class="item">Finances</div>
                            <div class="item">Job Apps</div>
                            <div class="item">Home Chores</div>
                          </div>
                </div>
                <br></br>
                <input type="date" value={date} onChange={(e) => setDate(e.target.value)} /> 

              </list>
              </div>
            {/* </div> */}

          </div>

          return (
        <div  className="dropdown" style = {{background:"red",width:"200px"}} >
         <div className="button" onClick={this.showDropdownMenu}> My Setting </div>

          { this.state.displayMenu ? (
          <ul>
         <li><a className="active" href="#Create Page">Create Page</a></li>
         <li><a href="#Manage Pages">Manage Pages</a></li>
         <li><a href="#Create Ads">Create Ads</a></li>
         <li><a href="#Manage Ads">Manage Ads</a></li>
         <li><a href="#Activity Logs">Activity Logs</a></li>
         <li><a href="#Setting">Setting</a></li>
         <li><a href="#Log Out">Log Out</a></li>
          </ul>
        ):
        (
          null
        )
        }

       </div>

    );
  }
      <button className="ui teal right labeled icon button" onClick={addTodo}>
        <i className="plus icon"></i>
        add
      </button>
    </div>
  </div>
</div>



};

const Item = (props) => {
  const {done , desc, _id} = props.todo;
  const deleteTodo = async () => {
    await fetch(`http://localhost:3001/todos/${_id}`, {
      method:'DELETE'
    }
    );
    props.fetchTodos();
  }

  const toggleDone = async () => {
    await fetch(`http://localhost:3001/todos/${_id}`, {
      method:'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ done: !done, desc})
    });

    props.fetchTodos();
  };


  return <div className='item'>
    {
      done ? 
      <i className="left floated green check square outline icon" onClick={toggleDone}></i> : 
      <i className="left floated square outline icon" onClick={toggleDone}></i>
    }
    {
      done ?
      <del > {desc}</del> :  <p > {desc}</p>
    }
    {done}
    <i className="right floated red trash icon" onClick={deleteTodo}></i>

  </div>
}

//  ------------------------------------------------ ------------------------------------------------



export default App;
